class NewsResponse {
  NewsResponse({
      this.status, 
      this.totalResults, 
      this.articles,
      this.message,
      this.code
  });

  NewsResponse.fromJson(dynamic json) {
    status = json['status'];
    code = json['message'];
    message = json['code'];
    totalResults = json['totalResults'];
    if (json['articles'] != null) {
      articles = [];
      json['articles'].forEach((v) {
        articles?.add(News.fromJson(v));
      });
    }
  }
  String? status;
  int? totalResults;
  List<News>? articles;
  String? code;
  String? message;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['status'] = status;
    map['totalResults'] = totalResults;
    if (articles != null) {
      map['articles'] = articles?.map((v) => v.toJson()).toList();
    }
    return map;
  }

}

